Events.OnObjDestroy:
(block
	(
		(anchored (objGetData gSource 'ADT_anchoredShip))
		(multipleAnchored (= (typeOf anchored) 'list))
	)
	(if multipleAnchored
		(block
			(
				(remaining (map anchored 'excludeNil anAnchored (if (= aDestroy (objGetObjByID anAnchored)) () anAnchored)))
			)
			(if (gr (count remaining) 0)
				(objSetData gSource 'ADT_anchoredShip remaining)
				(objRemove gSource)
			)
		)
		(if (= aDestroy (objGetObjByID anchored))
			;;remove if our object is destroyed
			(objRemove gSource)
		)
	)
)

Events.OnEventHandlerInit:
(block
	(
		(target (obj@ gSource "adt.objCreate.huntTarget"))
	)
	(if target
		(block ()
			(shpOrder gSource 'attack target)
			(objSet@ gSource "adt.objCreate.curTarget" (objGetId target))
		)
		(shpOrder gSource 'gate)
	)
)

Events.OnOrdersCompleted:
(block
	(
		(target (obj@ gSource "adt.objCreate.huntTarget"))
	)
	(if target
		(block ()
			(shpOrder gSource 'attack target)
			(objSet@ gSource "adt.objCreate.curTarget" (objGetId target))
		)
		(block ()
			(shpOrder gSource 'gate)
			(objSet@ gSource "adt.objCreate.curTarget" ())
		)
	)
)
